2.3.4.7 NIO群聊系统

服务端
public class Server {
    // 属性定义
    private Selector selector;
    private ServerSocketChannel listenChannel;
    private static final int PORT = 6667;
    private static final Logger LOGGER = LoggerFactory.getLogger(Server.class);

    public static void main(String[] args) throws Exception{
        // 启动客户端
        Server server = new Server();
        // 监听
        server.listen();
    }

    // 初始化
    public Server() {
        try {
            // 设置selector
            this.selector = Selector.open();
            // 设置channel
            listenChannel = ServerSocketChannel.open();
            // 绑定端口
            listenChannel.bind(new InetSocketAddress(PORT));
            // 设置非阻塞
            listenChannel.configureBlocking(false);
            // 将channel注册到selector
            listenChannel.register(selector, SelectionKey.OP_ACCEPT);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    // 监听
    public void listen() {
        try {
            while (true) {
                int count = selector.select();
                if (count > 0) { // 有事件处理
                    // 遍历获取selectedKey
                    Iterator iterator = selector.selectedKeys().iterator();
                    while (iterator.hasNext()) {
                        SelectionKey key = iterator.next();
                        // 对key的事件类型进行判断
                        if (key.isAcceptable()) {
                            SocketChannel socketChannel = listenChannel.accept();
                            // 设置channel非阻塞
                            socketChannel.configureBlocking(false);
                            // 将socketChannel注册到selector
                            socketChannel.register(selector,SelectionKey.OP_READ);
                            // 消息提示
                            LOGGER.info(socketChannel.getRemoteAddress().toString() + "上线");
                        }
                        if (key.isReadable()) {
                            // 读取客户端消息
                            readData(key);
                        }
                        // 从set中删除key
                        iterator.remove();
                    }
                } else {
                    LOGGER.info("等待...");
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    // 读取客户端消息
    private void readData(SelectionKey selectionKey) {
        // 定义socketChannel
        SocketChannel socketChannel = null;
        try {
            // 通过key获取channel
            socketChannel = (SocketChannel) selectionKey.channel();
            // 创建buffer
            ByteBuffer buffer = ByteBuffer.allocate(1024);
            // 将channel中的数据读取到buffer
            int count = socketChannel.read(buffer);
            // 判断是否读取到数据
            if (count > 0) {
                String msg = new String(buffer.array());
                LOGGER.info(msg);
                // 向其他客户端转发消息
                sendMsgToOther(msg,socketChannel);
            }

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    // 转发消息到其他客户端(需要排除自身的channel)
    private void sendMsgToOther(String msg,SocketChannel self) {
        LOGGER.info("服务器转发消息...");
        // 遍历所有注册到selector上的channel并排除自身
        selector.keys().stream().map(key -> ((SocketChannel) key.channel())).filter(channel -> channel == self).forEach(channel -> {
            try {
                channel.write(ByteBuffer.wrap(msg.getBytes()));
            } catch (IOException e) {
                try {
                    LOGGER.info(channel.getRemoteAddress().toString() + "已离线");
                    // 取消注册
                    channel.keyFor(selector).cancel();
                    // 关闭通道
                    channel.close();
                } catch (IOException ioException) {
                    ioException.printStackTrace();
                }
            }
        });
    }
}


客户端
public class Client {
    // 定义属性
    private final static String HOST = "127.0.0.1";
    private final static int PORT = 6667;
    private Selector selector;
    private SocketChannel socketChannel;
    private String username;

    private static final Logger LOGGER = LoggerFactory.getLogger(Client.class);

    public static void main(String[] args) {
        // 启动客户端
        Client client = new Client();
        // 启动一个线程，每隔3秒从服务器读取数据
        new Thread(() -> {
            while (true) {
                client.receiveInfo();
                try {
                    Thread.sleep(3000);
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
            }
        }).start();

        // 客户端向服务端发送数据
        Scanner scanner = new Scanner(System.in);

        while (scanner.hasNextLine()) {
            String msg = scanner.nextLine();
            client.sendInfo(msg);
        }
    }

    // 初始化
    public Client() {
        try {
            selector = Selector.open();
            // 连接服务器
            socketChannel = SocketChannel.open(new InetSocketAddress(HOST, PORT));
            // 设置非阻塞
            socketChannel.configureBlocking(false);
            // 将channel注册到selector
            socketChannel.register(selector, SelectionKey.OP_READ);
            // 获取username
            username = socketChannel.getLocalAddress().toString();

            LOGGER.info("客户端" + username + "准备就绪");
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    // 向服务器发送消息
    public void sendInfo(String info) {
        try {
            int count = socketChannel.write(ByteBuffer.wrap(info.getBytes()));
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    // 读取从服务端回复的消息
    public void receiveInfo() {
        try {
            int readChannels = selector.select();
            if (readChannels > 0) { // 是否有可用通道
                Set selectionKeys = selector.selectedKeys();
                Iterator iterator = selectionKeys.iterator();
                while (iterator.hasNext()) {
                    SelectionKey key = iterator.next();
                    if (key.isReadable()) {
                        SocketChannel channel = (SocketChannel) key.channel();
                        ByteBuffer buffer = ByteBuffer.allocate(1024);
                        // 读取缓冲区中的数据
                        channel.read(buffer);
                        // 输出buffer中的信息
                        LOGGER.info(new String(buffer.array()).trim());
                    } else {
                        LOGGER.info("没有可用通道");
                    }
                    iterator.remove();
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}



